mapper.select:

select

from public.cmt_close_day
where close_dtime = #{closeDtime,jdbcType=TIMESTAMP}
  and tag_no = #{tagNo,jdbcType=VARCHAR}

SELECT tag_no, tag_data, input_type, seq_no, close_dtime FROM (SELECT cmm_input.input_tag_no AS tag_no, cmt_close_day.tag_data ,cmm_input.input_type, cmm_input.seq_no, cmt_close_day.close_dtime FROM cmm_input INNER JOIN cmt_close_day ON cmm_input.input_tag_no = cmt_close_day.tag_no WHERE cmt_close_day.tag_no IN
#{item1}
AND cmt_close_day.delete_flag = 0 AND cmm_input.input_type=1 AND cmt_close_day.close_dtime = #{targetDate,jdbcType=TIMESTAMP}) AS t1 UNION (SELECT cmm_input.input_tag_no AS tag_no, cmt_close_day.tag_data ,cmm_input.input_type, cmm_input.seq_no, cmt_close_day.close_dtime FROM cmm_input INNER JOIN cmt_close_day ON cmm_input.input_tag_no = cmt_close_day.tag_no WHERE cmt_close_day.tag_no IN
#{item4}
AND cmt_close_day.delete_flag = 0 AND cmm_input.input_type=4 AND cmt_close_day.close_dtime = to_timestamp(#{targetDate,jdbcType=TIMESTAMP}, 'YYYY-MM-DD hh24:mi:ss') + '-1 day') UNION (SELECT cmm_input.input_tag_no AS tag_no, cmt_close_hour.tag_data ,cmm_input.input_type, cmm_input.seq_no, cmt_close_hour.close_dtime FROM cmm_input INNER JOIN cmt_close_hour ON cmm_input.input_tag_no = cmt_close_hour.tag_no WHERE cmt_close_hour.tag_no IN
#{item0}
AND cmt_close_hour.delete_flag = 0 AND cmm_input.input_type=0 AND cmt_close_hour.close_dtime < to_timestamp(#{targetDate,jdbcType=TIMESTAMP}, 'YYYY-MM-DD hh24:mi:ss') + '1 day' AND cmt_close_hour.close_dtime >= #{targetDate,jdbcType=TIMESTAMP}) ORDER BY seq_no, close_dtime;

mapper.delete:

delete from public.cmt_close_day
where close_dtime = #{closeDtime,jdbcType=TIMESTAMP}
  and tag_no = #{tagNo,jdbcType=VARCHAR}

mapper.update:

update public.cmt_close_day
set tag_data = #{tagData,jdbcType=VARCHAR},
  qlty_cd = #{qltyCd,jdbcType=NUMERIC},
  coll_dtime = #{collDtime,jdbcType=TIMESTAMP},
  last_upd_user = #{lastUpdUser,jdbcType=VARCHAR},
  last_upd_dtime = now(),
  delete_flag = #{deleteFlag,jdbcType=NUMERIC}
where close_dtime = #{closeDtime,jdbcType=TIMESTAMP}
  and tag_no = #{tagNo,jdbcType=VARCHAR}
  and delete_flag = 0

mapper.insert:
with params(close_dtime, tag_no, tag_data, last_upd_user) AS ( values
(to_timestamp(#{item.closeDtime,jdbcType=TIMESTAMP}, 'YYYY-MM-DD'), #{item.tagNo,jdbcType=VARCHAR}, #{item.tagData,jdbcType=VARCHAR}, #{item.lastUpdUser,jdbcType=VARCHAR})
), upsert AS ( update cmt_close_day t set tag_data = params.tag_data, qlty_cd = 192, coll_dtime = now(), last_upd_user = params.last_upd_user, last_upd_dtime = now(), delete_flag = 0 from params where t.close_dtime = params.close_dtime and t.tag_no = params.tag_no returning t.close_dtime, t.tag_no ) insert into cmt_close_day () select params.close_dtime, params.tag_no, params.tag_data, 192, now(), params.last_upd_user, now(),0 from params where params.close_dtime not in ( select close_dtime from upsert ) or params.tag_no not in ( select tag_no from upsert )

with params(close_dtime, tag_data, tagLogicName1) AS ( values
(to_timestamp(#{item.closeDtime,jdbcType=TIMESTAMP}, 'YYYY-MM-DD'), #{item.tagData,jdbcType=VARCHAR}, #{item.tagLogicName1,jdbcType=VARCHAR})
), upsert AS ( update cmt_close_day t set tag_data = params.tag_data, qlty_cd = 192, coll_dtime = now(), last_upd_user = #{lastUpdUser,jdbcType=TIMESTAMP}, last_upd_dtime = now(), delete_flag = 0 from params inner join public.cmm_tag_master_v on params.tagLogicName1 = cmm_tag_master_v.tag_logic_name1 where t.close_dtime = params.close_dtime and cmm_tag_master_v.tag_logic_name1 = params.tagLogicName1 returning t.close_dtime, t.tag_no ) insert into cmt_close_day () select params.close_dtime, cmm_tag_master_v.tag_no, params.tag_data, 192, now(), #{lastUpdUser,jdbcType=TIMESTAMP}, now(),0 from params inner join public.cmm_tag_master_v on params.tagLogicName1 = cmm_tag_master_v.tag_logic_name1 where params.close_dtime not in ( select close_dtime from upsert ) or cmm_tag_master_v.tag_no not in ( select tag_no from upsert )
